import { useState, useEffect } from "react";
import TextField from '@mui/material/TextField';
import "./App.css";

function App() {
  const initialValues = { name: "", email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const email_regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const phone_regex = /^\+(?:[0-9] ?){6,14}[0-9]$/;
    if (!values.name) {
        errors.name = "Company Name is required!";
      }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!email_regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.pnumber) {
      errors.pnumber = "Phone No is required!";
    } else if (!phone_regex.test(values.pnumber)) {
      errors.pnumber = "This is not a valid Phone No format!";
    }

    return errors;
  };

  return (
    <div className="container">
      {Object.keys(formErrors).length === 0 && isSubmit ? (
        <div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
      )}

      <form onSubmit={handleSubmit}>

        <div
            style={{   display: "flex",
            flexDirection: 'row',
                justifyContent: "center",
                alignItems: "center",
                justifycontent: "space-between"

             }}
        >




        <div className="ui form">
        <div>

     <TextField
       required
       id="outlined-required"
       label="Company Name"
       name="company_name"
           placeholder="Company name"
           value={formValues.company_name}
           onChange={handleChange}

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Email"
       name="email"
           placeholder="Company Email"
           value={formValues.email}
           onChange={handleChange}

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Phone No"

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Address"

     />

   </div>
   </div>
   <div>
     <h3>Client Company Informations</h3>

   <div>
     <TextField
       required
       id="outlined-required"
       label="Client Company Name"

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Client Company Email"

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Client Company Phone No"

     />

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Client Company Address"

     />

   </div>
          <p>{formErrors.pnumber}</p>
          <button className="fluid ui button blue">Submit</button>
        </div>
        </div>
      </form>
    </div>
  );
}

export default App;
