import React from 'react'
import Box from '@mui/material/Box';
import { useState, useEffect } from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';


function App() {
  const initialValues = { company_name: "", email: "", pnumber: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const email_regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/;
    const phone_regex = /^\+(?:[0-9] ?){6,14}[0-9]$/;
    if (!values.name) {
        errors.name = "Company Name is required!";
      }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!email_regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.pnumber) {
      errors.pnumber = "Phone No is required!";
    } else if (!phone_regex.test(values.pnumber)) {
      errors.pnumber = "This is not a valid Phone No format!";
    }

    return errors;
  };

  return (
    <Box
      component="form"
      sx={{
        '& .MuiTextField-root': { m: 1, width: '25ch' },
      }}
      noValidate
      autoComplete="off"
    >
    <div>
      {Object.keys(formErrors).length === 0 && isSubmit ? (
        <div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
      )}

      <form onSubmit={handleSubmit}>

        <div
            style={{   display: "flex",
            flexDirection: 'row',
                justifyContent: "center",
                alignItems: "center",
                justifycontent: "space-between"

             }}
        >







        <div >
        <div>

     <TextField
       required
       id="outlined-required"
       label="Company Name"
       name="company_name"
           placeholder="Company name"
           value={formValues.company_name}
           onChange={handleChange}

     />
      <p>{formErrors.company_name}</p>

   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Email"
       name="email"
           placeholder="Company Email"
           value={formValues.email}
           onChange={handleChange}

     />


   </div>
   <p>{formErrors.email}</p>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Phone No"
       name='pnumber'
       placeholder="Company Phone Number"
       value={formValues.pnumber}
       onChange={handleChange}

     />
    <p>{formErrors.pnumber}</p>
   </div>
   <div>
     <TextField
       required
       id="outlined-required"
       label="Company Address"

     />

   </div>
   </div>
   <div>



   <Button variant="contained" >Submit</Button>
        </div>
        </div>
      </form>
    </div>
    </Box>
  );
}

export default App;
