import React from 'react'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { useState, useEffect } from "react";
import Button from '@mui/material/Button';


function Invoice_form() {
   const initialValues = { name: "", email: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const email_regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const phone_regex = /^\+(?:[0-9] ?){6,14}[0-9]$/;
    if (!values.name) {
        errors.name = "Company Name is required!";
      }
    if (!values.email) {
      errors.email = "Email is required!";
    } else if (!email_regex.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    if (!values.pnumber) {
      errors.pnumber = "Phone No is required!";
    } else if (!phone_regex.test(values.pnumber)) {
      errors.pnumber = "This is not a valid Phone No format!";
    }

    return errors;
  };

  return (
    <Box
      component="form"
      sx={{
        '& .MuiTextField-root': { m: 1, width: '25ch' },
      }}
      noValidate
      autoComplete="off"
      onSubmit={handleSubmit}
    >
    <div
       style={{   display: "flex",
       flexDirection: 'column',
           justifyContent: "center",
           alignItems: "center",
           justifycontent: "space-between"

        }}
    >
{Object.keys(formErrors).length === 0 && isSubmit ? (
        <div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
      )}

      <h3>Invoice Form</h3>
      <div>
      <TextField
          required
          id="filled-required"
          label="Company Name"
          value={formValues.company_name}
          onChange={handleChange}
        />
         <TextField
          required
          id="filled-required"
          label="Client Company Name"

        />
      </div>

      <div>
      <TextField
          required
          id="filled-required"
          label="Company Phone No"
        />
         <TextField
          required
          id="filled-required"
          label="Client Company Phone No"

        />
      </div>
      <div>
      <TextField
          required
          id="filled-required"
          label="Company Email"
        />
         <TextField
          required
          id="filled-required"
          label="Client Company Email"

        />
      </div>
      <div>
      <TextField
          required
          id="filled-required"
          label="Company Address"
        />
         <TextField
          required
          id="filled-required"
          label="Client Company Address"

        />
      </div>


      <Button variant="contained" >Submit</Button>
    </div>

    </Box>
  )
}

export default Invoice_form
